CREATE TABLE expense_types (id SERIAL PRIMARY KEY, created_at TIMESTAMPTZ DEFAULT NOW(), name VARCHAR NOT NULL);
CREATE TABLE suppliers (id SERIAL PRIMARY KEY, observations TEXT, created_at TIMESTAMPTZ DEFAULT NOW(), name VARCHAR NOT NULL, phone VARCHAR, contact_name VARCHAR, contact_phone VARCHAR);
CREATE TABLE works (id SERIAL PRIMARY KEY, total_value NUMERIC DEFAULT 0.00, name VARCHAR NOT NULL, address TEXT, reference_point TEXT, status TEXT DEFAULT 'active', created_at TIMESTAMPTZ DEFAULT NOW(), end_date_prediction DATE, start_date DATE);
CREATE TABLE people (id SERIAL PRIMARY KEY, role VARCHAR, description TEXT, service_type TEXT DEFAULT 'daily', phone VARCHAR, nickname VARCHAR, name VARCHAR NOT NULL, created_at TIMESTAMPTZ DEFAULT NOW());
CREATE TABLE services (id SERIAL PRIMARY KEY, work_id INTEGER NOT NULL, name VARCHAR NOT NULL, percentage_work NUMERIC DEFAULT 0.00, value NUMERIC DEFAULT 0.00, executed_percentage NUMERIC DEFAULT 0.00, status TEXT DEFAULT 'pendente', paid_value NUMERIC DEFAULT 0.00, created_at TIMESTAMPTZ DEFAULT NOW());
CREATE TABLE materials (id SERIAL PRIMARY KEY, work_id INTEGER NOT NULL, supplier_id INTEGER, name VARCHAR NOT NULL, expense_type_id INTEGER, amount NUMERIC NOT NULL, purchase_date DATE NOT NULL, is_paid BOOLEAN DEFAULT FALSE, created_at TIMESTAMPTZ DEFAULT NOW());
CREATE TABLE person_payments (id SERIAL PRIMARY KEY, person_id INTEGER NOT NULL, work_id INTEGER, amount NUMERIC NOT NULL, payment_date DATE NOT NULL, description VARCHAR, is_paid BOOLEAN DEFAULT FALSE, created_at TIMESTAMPTZ DEFAULT NOW());
CREATE TABLE revenues (id SERIAL PRIMARY KEY, work_id INTEGER NOT NULL, service_id INTEGER, description VARCHAR NOT NULL, amount NUMERIC NOT NULL, received_date DATE NOT NULL, status TEXT DEFAULT 'to_receive', created_at TIMESTAMPTZ DEFAULT NOW());
CREATE TABLE sub_services (id SERIAL PRIMARY KEY, service_id INTEGER NOT NULL, name VARCHAR NOT NULL, percentage_service NUMERIC DEFAULT 0.00, value NUMERIC DEFAULT 0.00, executed_percentage NUMERIC DEFAULT 0.00, created_at TIMESTAMPTZ DEFAULT NOW());
CREATE TABLE tasks (id SERIAL PRIMARY KEY, work_id INTEGER NOT NULL, responsible_id INTEGER, title VARCHAR NOT NULL, description TEXT, status TEXT DEFAULT 'todo', priority TEXT DEFAULT 'medium', deadline DATE, column_index INTEGER DEFAULT 0, created_at TIMESTAMPTZ DEFAULT NOW());
CREATE TABLE users (id SERIAL PRIMARY KEY, name VARCHAR NOT NULL, email VARCHAR NOT NULL, password VARCHAR NOT NULL, role TEXT DEFAULT 'user', must_change_password BOOLEAN DEFAULT FALSE, created_at TIMESTAMPTZ DEFAULT NOW());
CREATE TABLE user_permissions (id SERIAL PRIMARY KEY, user_id INTEGER NOT NULL, resource VARCHAR NOT NULL, can_list BOOLEAN DEFAULT FALSE, can_create BOOLEAN DEFAULT FALSE, can_read BOOLEAN DEFAULT FALSE, can_update BOOLEAN DEFAULT FALSE, can_delete BOOLEAN DEFAULT FALSE);
CREATE TABLE work_suppliers (id SERIAL PRIMARY KEY, work_id INTEGER NOT NULL, supplier_id INTEGER NOT NULL, created_at TIMESTAMPTZ DEFAULT NOW());

